import {Pool} from 'pg';
import { getDatabaseConfig } from './config';


const config = getDatabaseConfig();

const pool = new Pool({
  host: config.host,
    port: config.port,
    database: config.database,
    user: config.user,
    password: config.password,
    min: config.poolMin,
    max: config.poolMax,
    idleTimeoutMillis: config.poolIdleTimeout,
})

 export default pool;